	<!--SweetAlert-->
	<script src="//cdn.jsdelivr.net/npm/sweetalert2@11"></script>
	<script>
		/*
        Send data user
        */
		function sendDataUser(cveusuario, pwd) {

			if (cveusuario != '' && pwd != '') {

				$.ajax({

					url: "<?php echo base_url('Login/isValidUserPass'); ?>",
					type: "POST",
					datatype: "json",
					data: {

						cveusuario: cveusuario,
						pwd: pwd

					},

					success: function(response) {

						if (parseInt(response.is_valid_user_pass) == 0) {

							if (parseInt(response.error) == 1) {

								generateAlert('error', 'Ocurrió un error', 'La solicitud no puede ser procesada por el momento. Consulta al administrador del sistema.', 'TOP');

							}

							generateAlert('warning', 'Usuario y/o contraseña incorrectos', 'Por favor verifique los datos ingresados.', 'TOP');

						}

						if (parseInt(response.is_valid_user_pass) == 1) {

							location.replace("<?php echo base_url('Inicio/administrador'); ?>");

						}

					},

					error: function() {

						generateAlert('error', 'Ocurrió un error en la red', 'La solicitud no puede ser procesada por el momento. Consulta al administrador del sistema.', 'TOP');

					}

				});

			} else {

				generateAlert('warning', 'Datos faltantes', 'Ingrese por favor su usuario y contraseña.', 'TOP');

			}

		}
		/*
		SweetAlert
		*/
		function generateAlert(var_icon, var_title, var_text, var_position) {

			Swal.fire({
				icon: var_icon,
				title: var_title,
				text: var_text,
				timer: 9000,
				timerProgressBar: true,
				position: var_position,
				backdrop: false,
				confirmButtonText: 'OK',
				confirmButtonColor: '#235B4E',
			})

		}
	</script>
	<script>
		/*
        View password
        */

		const togglePassword = document.querySelector("#togglePassword");

		const password = document.querySelector("#pwd");

		togglePassword.addEventListener("click", function() {

			const type = password.getAttribute("type") === "password" ? "text" : "password";

			password.setAttribute("type", type);

			if (password.getAttribute("type") === "password") {

				this.setAttribute("class", "fas fa-solid fa-eye-slash");

			}

			if (password.getAttribute("type") === "text") {

				this.setAttribute("class", "fas fa-solid fa-eye");

			}

		});
	</script>

